window.addEventListener('load', function () {


	//region Popup календарь
	var btn = document.querySelector('.m-calendar-button');

	if (btn) {
		var calendarEl = btn.nextElementSibling.querySelector('.m-calendar');

		var currentDate = new Date();
		var currentDateFormat = currentDate.toISOString().slice(0, 10);
		var currentDateYear = currentDate.getFullYear();

		// Для дня рождения
		if (calendarEl.classList.contains('m-calendar_birthday')) {
			var birthdayYear = document.querySelector('.form-item-field-user-bdate-und-0-value-year select').value;
			var birthdayMonth = document.querySelector('.form-item-field-user-bdate-und-0-value-month select').value;
			var birthdayDay = document.querySelector('.form-item-field-user-bdate-und-0-value-day select').value;

			// Редкий случай, когда заполнили только год рождения
			if (birthdayYear) {
				currentDateFormat = birthdayYear + '-01-01';
				// Когда есть все данные
				if (birthdayMonth && birthdayDay) {
					currentDateFormat = birthdayYear + '-' + birthdayMonth + '-' + birthdayDay;
				}
			}
			else {
				// текущий год минус 18, формат yyyy-mm-dd
				currentDateFormat = currentDateYear - 18 + '-01-01';
			}
		}

		// Инициализация календарей
		var calendar = new TavoCalendar(calendarEl, {
			locale: 'ru',
			past_select: true,
			date: currentDateFormat
		});

		// Если меняем год в селекте
		const yearField = document.querySelector('.form-item-field-user-bdate-und-0-value-year select');
		if (yearField) {
			yearField.addEventListener('change', (event) => {
				var newCurrentDateFormat = currentDateFormat.substring(4);
				currentDateFormat = event.target.value + newCurrentDateFormat;

				// Очищаем вручную div с календарём
				document.querySelector('.m-calendar_birthday.tavo-calendar').innerHTML = "";

				// Инициализируем новый календарь, т.к. нет методов set
				calendar = new TavoCalendar(calendarEl, {
					locale: 'ru',
					past_select: true,
					date: currentDateFormat
				});
			});
		}

		var formSelects = btn.parentElement.parentElement.querySelectorAll('.form-item select');

		// Для дня рождения
		if (calendarEl.classList.contains('m-calendar_birthday')) {
			formSelects = btn.parentElement.parentElement.parentElement.querySelectorAll('.form-item select');
			var setDate = function setDate(str) {
				var arr = str.split('-').reverse();
				formSelects[0].customSelect.value = parseInt(arr[0]);
				formSelects[1].customSelect.value = parseInt(arr[1]);
				formSelects[2].customSelect.value = parseInt(arr[2]);
			};
		}

		calendarEl.addEventListener('calendar-select', function () {
			setDate(calendar.getSelected());
			calendarEl.parentElement.classList.remove('m-calendar-wrapper_visible');
		});
		btn.addEventListener('click', function (e) {
			e.preventDefault();
			btn.nextElementSibling.classList.toggle('m-calendar-wrapper_visible');
		});
	}

	//endregion
});


